import { Link } from "react-router-dom"
import React from 'react';
import { DateTime } from 'luxon'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCalendarAlt } from '@fortawesome/free-solid-svg-icons'
import DayPicker from 'react-day-picker';
import 'react-day-picker/lib/style.css';


const Schedule = () => {
    const [dayPicker, setDayPicker] = React.useState(false)

    const handleDisplayDayPicker = (e) => {
        let isToggleOn = false
        if (e)  {
            isToggleOn = !isToggleOn
        }
        setDayPicker(isToggleOn)
    }

    
    const day = DateTime.now()
    console.log(day)
    

    return (

        <div className = 'schedule'>
            <nav>
                <ul>
                    <li>
                        <Link to = '/'>
                            <span style = {{ background: 'grey', padding: '0.2rem', color: 'black', borderRadius: '4px'}}>LIVE</span>
                        </Link>
                    </li>
                    <li>
                        <Link to = '/'>
                        {`${DateTime.now().minus({days: 2}).c.day}/${DateTime.now().c.month}`} 
                        </Link>
                    </li>
                    <li>
                        <Link to = '/'>
                        {`${DateTime.now().minus({days: 1}).c.day}/${DateTime.now().c.month}`}
                        </Link>
                    </li>
                    <li>
                        <Link to = '/'>
                        {`${DateTime.now().c.day}/${DateTime.now().c.month}`}
                        </Link>
                    </li>
                    <li>
                        <Link to = '/'>
                        {`${DateTime.now().plus({days: 1}).c.day}/${DateTime.now().c.month}`}
                        </Link>
                    </li>
                    <li>
                        <Link to = '/'>
                        {`${DateTime.now().plus({days: 2}).c.day}/${DateTime.now().c.month}`}
                        </Link>
                    </li>
                    <li>
                        <span>
                            <FontAwesomeIcon onClick = {handleDisplayDayPicker} icon = {faCalendarAlt} />
                            {/* <input type = 'date' /> */}
                        </span>
                    </li>
                </ul>
                {dayPicker ? <DayPicker /> : '' }
            </nav>
        </div>
    )
}

export default Schedule
