        //             </div>
        //         </div>
        //     </div>
        // )
        return (
            <div key={index} className='fixtures_container'>
                <div  className='comp_header'>
                    <img className='competition_logo' src={fixture.league.flag} alt={fixture.league.flag} width='20' height='15' />
                    <Link  className = 'competition_name' >{fixture.league.country}</Link>
                    <span className = 'league_group'>{fixture.league.name} - {fixture.league.round}</span>
                    <span className = 'date'>{AppendToday()}</span>
                </div>
                <div className='fixtures'>
                    <div className='live-match'></div>
                    <div className='timestamp'>00:00</div>
                    <div className = 'teamOne_teamTwo_scoreSheet'>
                        <div className = 'teams'>
                            <div className = 'team'>{fixture.teams.home.name}</div>
                            <div className = 'score'>{fixture.goals.home ? fixture.goals.home : '?'}</div>
                        </div>
                        <div className = 'teams'>
                            <div className = 'team'>{fixture.teams.away.name}</div>
                            <div className = 'score'>{fixture.goals.away ? fixture.goals.away : '?'}</div>
                        </div>
                    </div>
                    <div className = 'star'>
                          <FontAwesomeIcon icon = {faStar} />
                    </div>
                </div>
            </div>
        )
    })
    
