import React from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faStar} from '@fortawesome/free-solid-svg-icons'
import {AppendToday} from '../Utils/AppendDay'
import { Link } from 'react-router-dom'
import useDeepCompareEffect from 'use-deep-compare-effect'

const CompetitionHeader = ({fixtures}) => {
    const [allLeagues, SetAllLeagues] = React.useState([])
    const [countries, setCountries] = React.useState([])
    const [matches, setMatches] = React.useState([])

    console.log({fixtures})
    const setIdResponse = new Set()

    
    useDeepCompareEffect(() => {
        const setCountriesObj = new Set()
        for (let i=0; i<fixtures.length; i++)   {
            const element = fixtures[i].league.country
            setCountriesObj.add(element)
        }
        const countriesArr = Array.from(setCountriesObj)
        setCountries(countriesArr)
        console.log( 'countriesArr',countriesArr)
    
        const setMatchesObj = new Set()
        for (let i=0; i<fixtures.length; i++)   {
            const element = fixtures[i].teams
            setMatchesObj.add(element)
        }
        const matchesArr = Array.from(setMatchesObj)
        setMatches(matchesArr)
        console.log('matches', matches)

        for (let i = 0; i < fixtures.length; i++)   {
            const element = fixtures[i].league.id
            setIdResponse.add(element)
        }
        console.log(setIdResponse)

        const uniqueIds = Array.from(setIdResponse)
        // console.log(uniqueIds);
        const sortedLeagues = [];
        for (let i=0; i<uniqueIds.length; i++) {
        const id = uniqueIds[i];
        const leagueObj = {leagueName:  fixtures[i].league.name,
            leagueFlag: fixtures[i].league.flag,
            leagueRound:    fixtures[i].league.round,
            leagueCountry:  fixtures[i].league.country,
            matches: []
                            }
        const arrOfObj = fixtures.filter(fixture => {
            if (fixture.league.id !== id)   return
            return fixture.teams;
        });
        leagueObj.matches = arrOfObj
        sortedLeagues.push(leagueObj)
        
        
    }
    SetAllLeagues(sortedLeagues)
    
    
    }, [allLeagues, fixtures, setIdResponse, countries, matches])
    
    console.log(allLeagues)
    const Content = () => countries?.length && countries.map((country) => {
        
        return allLeagues.map((league) => {
            if (league.league.country !== country) return;
           return matches.map((match, index) => {
                if (match.league.league !== league) return;
                return 
            })
        }) (
            <div key={index} className='fixtures_container'>
                <div className='comp_header'>
                    <div className='img_container'>
                        <img 
                        className='competition_logo'
                        src={league.leagueFlag}
                        alt={`flag of ${league.leagueCountry}`}
                        width='20' height='15'
                        />
                    </div> 
                    <Link  className='competition_name'>{league.leagueCountry}</Link>
                    <Link className='league_group'>{league.leagueName} - {league.leagueRound}</Link>
                    <span className='date'>{AppendToday()}</span>
                </div>
                
               {league.matches.map((match, subIndex) => {
                   return   (
                    <div key={subIndex} className='fixtures'>
                    <div className='live_match' style={{backgroundColor: match.fixture.status.elapsed 
                                                        && match.fixture.status.long !== 'Match Finished' 
                                                        ? 'orange' : 'black'}}></div>
                    <div className='timestamp'>{match.fixture.status.long === 'Not Started' ? ''
                                                :match.fixture.status.long === 'Time to be defined' ? ''
                                                :match.fixture.status.long === 'Match Postponed' ? 'postp'
                                                :match.fixture.status.long === 'Match Finished' ? 'FT'
                                                : `${match.fixture.status.elapsed}'`}
                    </div>
                    <div className='teamOne_teamTwo_scoreSheet'> 
                        <div className='teams'>
                            <div className='team'>{match.teams.home.name}</div>
                            <div className='score'>{match.fixture.status.long === 'Not Started' ? '?'
                                                    : match.fixture.status.long === 'Match Finished' ? match.goals.home
                                                    : match.fixture.status.elapsed ? match.goals.home
                                                    : '?'}
                            </div>
                        </div>
                        <div className='teams'>
                            <div className='team'>{match.teams.away.name}</div>
                            <div className='score'>{match.fixture.status.long === 'Not Started' ? '?'
                                                    : match.fixture.status.long === 'Match Finished' ? match.goals.away
                                                    : match.fixture.status.elapsed ? match.goals.away
                                                    : '?'}
                            </div>
                        </div>
                    </div>
                    <div className='star'>
                        <FontAwesomeIcon icon = {faStar} />
                    </div>
                </div>
                   )
               })}     
               

            </div>
        )
    })

    if (!fixtures.length) return <div>No events available right now</div>;
    return <Content />

}

export default CompetitionHeader
