import React from 'react'


const CompetitionHeader = ({fixtures}) => {
   
    const [date, setDate] = React.useState(Date().getDate())
    const [month, setMonth] = React.useState(Date().getMonth())
    const [year, setYear] = React.useState(Date().getFullYear())
    
   

    return (
        <div>
            {fixtures.length && fixtures.map((fixture, index) => {
                return (

            <div key = {index} className = 'comp_header'>
                <img src={fixture.league.flag} alt={fixture.league.flag} width='20' height='15' />
                <span className = 'competition_name'>{fixture.league.country} - </span>
                <span className = 'league_group'>{fixture.league.name} - {fixture.league.round}</span>
                <span className = 'date'>{date}/{month}/{year}</span>
            </div>
                )
            })}
        </div>
            
    )   
    
}

export default CompetitionHeader
