import React from 'react'
import Schedule from './Schedule'
import CompetitionHeader from './CompetitionHeader'
import { DateTime} from 'luxon'


const Home = () => {
    const [fixtures, setFixtures] = React.useState([])
    const [date, setDate] = React.useState(Date.now())

    const dayPicker = `${DateTime.now().c.day}/${DateTime.now().c.month}/${DateTime.now().c.year}`
        console.log(dayPicker)
    setDate(dayPicker)
    React.useEffect(() => {

        const fetchFixturesByDate = async () => {
            const response = await fetch(`https://api-football-v1.p.rapidapi.com/v3/fixtures?date=${date}`, {
                "method": "GET",
                "headers": {
                    "x-rapidapi-key": "417fbcfbbfmsh1a4fc29dfadaa6dp16235bjsn290cb241d7cc",
                    "x-rapidapi-host": "api-football-v1.p.rapidapi.com"
                }
            })
            // .catch(err => {
            //     console.error(err);
            // });
            const data = await response.json()
            setFixtures(data.response)
            console.log(data.response)
        
        }
             fetchFixturesByDate()
    }, [])

    return (
            <div className = 'container'>
                <Schedule fixtures = {fixtures} />
                <CompetitionHeader fixtures = {fixtures} />
            </div>
    )
}

export default Home
