import React from 'react';
import NewsNavBar from './NewsNavBar';

const News = () =>  {
    const [headlines, setHeadlines] = React.useState([])

    
    React.useEffect(() => {

        const fetchHeadlines = async () => {
           
            const resp = await fetch("https://free-football-soccer-videos1.p.rapidapi.com/v1/", {
                "method": "GET",
                "headers": {
                    "x-rapidapi-key": "417fbcfbbfmsh1a4fc29dfadaa6dp16235bjsn290cb241d7cc",
                    "x-rapidapi-host": "free-football-soccer-videos1.p.rapidapi.com"
                }
            })
            // .catch(err => {
            //     console.error(err);
            // });
            // console.log(data.response)
            
            const data = await resp.json()
           data?.length && setHeadlines(data)
        }
           fetchHeadlines()
        
        
        }, [] )
        console.log(headlines)

    return  (
        <div className='news-container'>
            <div className='news_navBar'>
                <NewsNavBar />
            </div>

            <section className='thumbnail_section'>
            {headlines.map((headline, index) => {
            return (
                <div key={index} className='highlight_card'>
                    <div className='card_image'>
                        <img src={headline.thumbnail} alt={`thumbnail ~${headline.title}`} />
                        <span className='card_tag'></span>
                        <h6 className='card_title'>{headline.competition.name} - {headline.title}</h6>
                        <span className='card_timestamp'></span>
                    </div>
                </div>
            )
        })}
            </section>
            <h4>Top Stories</h4>
            <hr />
            <section>
                <div className="card_preview">
                    <div className="preview_image">
                        <img src="" alt="" />
                    </div>
                    <h6 className='preview_title'>preview title</h6>
                    <span className="preview_tag"></span>
                    <span className="preview_timestamp"></span>
                </div>
                <hr />
            </section>
        </div>
    )
}

export default News
